import React, { useEffect, useState } from "react";
import NavbarComponent from "../NavbarComponent/navbarComponent";
import axios from "axios";
import { Container, Row, Col, Card, Button, Modal, Form } from "react-bootstrap";

const HomePage = () => {
    interface Task {
        _id: number,
        email: string,
        title: string,
        description: string,
        type_task: "ToDo" | "In Process" | "Success",
        date_creation: string
    }
    type Props = {
        _id: number,
        email: string,
        title: string,
        description: string,
        type_task: "ToDo" | "In Process" | "Success",
        date_creation: string
    }
    const [table, setTable] = useState<Task[]>([]);
    const [show, setShow] = useState(false)
    const [data, setData] = useState({
        email: '',
        title: '',
        description: '',
        type_task: '',
        date_creation: ''
    })
    const handleNewTask = () => {
        console.log(data);
    }
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        getAllTasks();
    }, [])

    const getAllTasks = async () => {
        const user = localStorage.getItem("loggedUser");
        let userJson;
        if (user) {
            userJson = JSON.parse(user);
        }
        const headersConfig = {
            'Accept': 'application/json',
            'Authorization': 'Bearer ' + userJson.token
        }
        await axios({
            method: 'post',
            url: 'http://localhost:3000/allTasks',
            data: { email: userJson.email },
            headers: headersConfig
        }).then(response => {
            setTable(response.data);
        }
        ).catch(error => {
            console.log(error)
        })
    }

    const Buttons = () => {
        return (
            <Row>
                <Col className="d-grid gap-2 mt-2" lg="6">
                    <Button variant="secondary">Update</Button>
                </Col>
                <Col className="d-grid gap-2 mt-2" lg="6">
                    <Button variant="danger">Delete</Button>
                </Col>
            </Row>
        );
    }

    const Cards = (props: Props) => {
        let cardStyle = {
            width: '18rem',
            backgroundColor: ''
        }
        switch (props.type_task) {
            case 'ToDo': cardStyle.backgroundColor = '#8ADBFE'; break;
            case 'In Process': cardStyle.backgroundColor = '#FEFE8A'; break;
            case 'Success': cardStyle.backgroundColor = "#8AFE98"; break;
        }
        return (
            <Card style={cardStyle}>
                <Card.Body>
                    <Card.Title>{props.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">Fecha de creaci칩n: {props.date_creation}</Card.Subtitle>
                    <Card.Text>{props.description}</Card.Text>
                    <Card.Footer className="text-muted">Status: {props.type_task}</Card.Footer>
                    <Buttons />
                </Card.Body>
            </Card>
        );
    }
    const FormTask = () => {
        const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
            setData({ ...data, [e.target.name]: e.target.value });
        }
        const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
            data.type_task = e.target.value;
        }
        return (
            <>
                <Modal
                    show={show}
                    onHide={handleClose}
                    backdrop="static"
                    keyboard={false}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>New Task</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group className="mb-3" controlId="formTitle">
                                <Form.Label style={{ fontWeight: "bold" }}>Status:</Form.Label>
                                <Form.Select name="type_task" onChange={handleSelectChange} aria-label="Select Status">
                                    <option value="empty">Selecciona una opci칩n</option>
                                    <option value="ToDo">ToDo</option>
                                    <option value="In Process">In Process</option>
                                    <option value="Sucess">Sucess</option>
                                </Form.Select>
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formTitle">
                                <Form.Label style={{ fontWeight: "bold" }}>Title:</Form.Label>
                                <Form.Control type="text" name="title" onChange={handleInputChange} placeholder="Ingresa el titulo de tu Task" />
                            </Form.Group>
                            <Form.Group className="mb-3" controlId="formDescription">
                                <Form.Label style={{ fontWeight: "bold" }}>Description: </Form.Label>
                                <Form.Control as="textarea" onChange={handleInputChange} name="description" rows={3} placeholder="Ingresa la descripci칩n de tu Task (m치s detallado, mejor)" />
                            </Form.Group>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Cancelar
                        </Button>
                        <Button variant="primary" onClick={handleNewTask}>Guardar Task</Button>
                    </Modal.Footer>
                </Modal>
            </>
        );
    }
    if (table.length !== 0) {
        return (
            <>
                <NavbarComponent />
                <FormTask />
                <Container className="mt-3 justify-content-md-center">
                    <h1 className="mt-3">My Tasks</h1>
                    <Button variant="primary" onClick={handleShow}>Crear una nueva Task</Button>
                    <Row className="justify-content-md-center">
                        {table.map((dataItem) => (
                            <Col className="mt-3" key={dataItem._id}>
                                <Cards _id={dataItem._id} email={dataItem.email} title={dataItem.title} description={dataItem.description} type_task={dataItem.type_task} date_creation={dataItem.date_creation} />
                            </Col>
                        ))}
                    </Row>
                </Container>
            </>
        );
    }
    else {
        return (
            <>
                <NavbarComponent />
                <FormTask />
                <Container className="mt-3">
                    <h1 className="mt-3">My Tasks</h1>
                    <Button variant="primary">Crear una nueva Task</Button>
                    <Row className="justify-content-md-center">
                        <h2>Is Empty :(</h2>
                        <h2>Create a new Task.</h2>
                    </Row>
                </Container>
            </>
        )
    }
}
export default HomePage;
