import React, { useEffect, useState } from "react";
import NavbarComponent from "../NavbarComponent/navbarComponent";
import axios from "axios";
import { Container, Row, Col, Card } from "react-bootstrap";

const HomePage = () => {
    interface Task {
        _id: number,
        email: string,
        title: string,
        description: string,
        type_task: "ToDo" | "In Process" | "Success",
        date_creation: string
    }
    const [table, setTable] = useState<Task[]>([]);
    useEffect(() => {
        getAllTasks();
    }, [])
    const getAllTasks = async () => {
        const user = localStorage.getItem("loggedUser");
        let userJson;
        if (user) {
            userJson = JSON.parse(user);
        }
        const headersConfig = {
            'Accept': 'application/json',
            'Authorization': 'Bearer ' + userJson.token
        }
        await axios({
            method: 'post',
            url: 'http://localhost:3000/allTasks',
            data: {email: userJson.email},
            headers: headersConfig
        }).then(response => {
            setTable(response.data);
        }
        ).catch(error => {
            console.log(error)
        })
    }

    return (
        <>
            <NavbarComponent />
            <Container className="mt-4">
                <Row className="justify-content-md-center">
                    {table.map((dataItem) => (
                        <Col key={dataItem._id} className="mt-3">
                            <Card style={{ width: '18rem' }}>
                                <Card.Body>
                                    <Card.Title>{dataItem.title}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">{dataItem.date_creation}</Card.Subtitle>
                                    <Card.Text>
                                        {dataItem.description}
                                    </Card.Text>
                                    <Card.Footer className="text-muted">Status: {dataItem.type_task}</Card.Footer>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        </>
    );
}
export default HomePage;
